import { useState } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useLoading } from '../../contexts/LoadingContext';

function LoginForm() {
	const { login } = useAuth();
	const [startLoading, stopLoading] = useLoading();
	const [input, setInput] = useState({ emailOrMobile: '', password: '' });

	const handleChangeInput = (e) => {
		setInput({ ...input, [e.target.name]: e.target.value });
	};
	const handleSubmitForm = async (e) => {
		e.preventDefault();
		try {
			await login();
		} catch (err) {}
	};

	return (
		<form id='form' className='' onSubmit={handleSubmitForm}>
			<div className='row'>
				<div className='mb-3 col-md-6 justify-center'>
					<label>Email Address</label>
					<input
						type='text'
						className='form-control'
						name='emailOrMobile'
						value={input.emailOrMobile}
						onChange={handleChangeInput}
					/>
				</div>
				<div className='mb-3 col-md-6 justify-center'>
					<label>Password</label>
					<input
						type='text'
						className='form-control'
						name='password'
						value={input.password}
						onChange={handleChangeInput}
					/>
				</div>
				<button
					type='button'
					className='mt-3 mx-auto btn btn-outline-dark text-light col-md-5 offset-md-3'
				>
					Sign In
				</button>
			</div>
		</form>
	);
}
export default LoginForm;
