import { useEffect, useRef, useState } from 'react';
import { Modal as BsModal } from 'bootstrap';

function Modal({ children, open, title }) {
	const modalEl = useRef();

	const [modal, setModal] = useState(null);

	useEffect(() => {
		const modalObj = new BsModal(modalEl.current);
	}, [open]);

	return (
		<div className='modal fade' tabIndex='-1' ref={modalEl}>
			<div className='modal-dialog modal-dialog-centered'>
				<div className='modal-content'>
					<div className='modal-header'>
						<button type='button' className='btn-close invisible'></button>
						<h5 className='modal-title'>{title}</h5>
						<button type='button' className='btn-close btn-dark'></button>
					</div>
					<div className='modal-body'>{children}</div>
				</div>
			</div>
		</div>
	);
}

export default Modal;
