import { useAuth } from '../../contexts/AuthContext';
import { useState } from 'react';
import { toast } from 'react-toastify';

import { useLoading } from '../../contexts/LoadingContext';

function LoginForm() {
	const { login } = useAuth();
	const { startLoading, stopLoading } = useLoading();

	const [input, setInput] = useState({
		emailOrMobile: '',
		password: '',
	});

	const handleChangeInput = (e) => {
		console.log(res.data.value);
		setInput({ ...input, [e.target.name]: e.target.value });
	};

	const handleSubmitForm = async (e) => {
		e.preventDefault();
		try {
			startLoading();
			await login(input);
			toast.success('success login');
		} catch (err) {
			toast.error(err.response.data.message);
		} finally {
			stopLoading();
		}
	};

	return (
		<div>
			<div className='p-2 bg-light border text-center'>
				<h1>Login form</h1>
			</div>

			<div className='container mt-4 mx-auto p-5'>
				<div className='row'>
					<div className='p-3 bg-warning bg-opacity-5 border border-dark border-start border-opacity-10 rounded-end shadow col-md-9 mx-auto'>
						<div className='col-md-6 offset-md-3'>
							<div className='signup-form'>
								<h5 className='mt-3 text-center'>ACCOUNT</h5>
								<form id='form' className='mt-3' onSubmit={handleSubmitForm}>
									<div className='row'>
										<div className='mb-3 col-md-6 justify-center'>
											<label>Email Address</label>
											<input
												type='text'
												className='form-control'
												name='emailOrMobile'
												value={input.emailOrMobile}
												onChange={handleChangeInput}
											/>
										</div>
										<div className='mb-3 col-md-6 justify-center'>
											<label>Password</label>
											<input
												type='text'
												className='form-control'
												name='password'
												value={input.password}
												onChange={handleChangeInput}
											/>
										</div>
										<button
											type='button'
											className='mt-3 mx-auto btn btn-outline-dark text-light col-md-5 offset-md-3'
										>
											Sign In
										</button>
									</div>
								</form>
								<p className='text-center mt-3 ms-8'>
									ALREADY REGISTERED,{' '}
									<a href='http://localhost:3000/register'>Register Now?</a>
								</p>
							</div>
						</div>
					</div>
				</div>
			</div>
		</div>
	);
}
export default LoginForm;
